import { configureStore } from '@reduxjs/toolkit';
import { chipsReducer, addChips, removeChips } from './slices/chipsSlice';

const store = configureStore({
    reducer: {
        "chips": chipsReducer,
    },
 });


export { store, addChips, removeChips};
