import Card from "./Cards";
import { useDispatch, useSelector } from "react-redux";


const PlayerHand = ()=>{
    const dispatch = useDispatch();
    const {hand, score} = useSelector((state: any) => state.player);
    const {dealStage} = useSelector((state: any) => state.game);

    return (
        <div className="bg-purple-300">
          <div>  
            <div className="flex align">  
              {dealStage? <>       
                {hand.map((card: any) => (
                  <Card code={card.code} />
                ))}        
              </>:
              <>

                <Card code={"back"}/>
                <Card code={"back"}/>
             </>}
              <h2>PlayerScore: {score} </h2>
            </div>
            </div>

        </div>
    )
  }
  export default PlayerHand;
