import React, { useState, useEffect } from "react";
import styles from '../index.module.css';
import Betting, { BettingState } from '../components/betting';

const Decision: React.FC = () => {
  const [betTotal, setBetTotal] = useState<number>(0);
  const [pot, setPot] = useState<number>(0);

  const updateBetTotal = (newBetTotal: number): void => {
    setBetTotal(newBetTotal);
  };

  const updatePot = (newPot: number): void => {
    setPot(newPot);
  };

  return (
    <>
      <div className={styles.row}>
        <div><h2>Bet total: {betTotal}</h2></div>
        <div><button className={styles.button}>Deal</button></div>
        <div><button className={styles.button}>Hit</button></div>
        <div><button className={styles.button}>Stay</button></div>
        <div><button className={styles.button}>Split</button></div>
        <div><button className={styles.button}>Double</button></div>
        <div><h2>Pot: {pot}</h2></div>
      </div>
      <Betting updateBetTotal={updateBetTotal} updatePot={updatePot} />
    </>
  );
};

export default Decision;
