    return (
        <>
        <div className="bg-blue-300">
        <div>  
            <div className="flex justify-center">
            {dealStage? <>
                {hand.map((card: any) => (
                    <div>
                                          <Card code={card.code} />


                    </div>
                ))}
              </>:
              <>

                <Card code={"back"}/>
                <Card code={"back"}/>
             </>}
             <h1>DealerScore: {score}</h1>
            </div>
            </div>
        </div>


        </>
    )
  }
  export default DealerHand;
