import { configureStore } from '@reduxjs/toolkit';
import { chipsReducer, addChips, removeChips, resetChips, doubleChips, winChips, splitChips } from './slices/chipsSlice';
import { gameReducer, setBetStage, setDealStage, setEvaluateStage, setScore, setSplitActive, setTextOutcome, setDealerTurnComplete, setGameOver, setHandsLost, setHandsWon, setHand1Dealt } from './slices/gameSlice';
import { playerReducer, setPlayerBet, setPlayerScore, playerDealCard, playerSplit, playerDealCard2, resetPlayerHand, setPlayerBet2 } from './slices/playerSlice';
import { dealerReducer, setDealerScore, dealerDealCard, resetDealerHand } from './slices/dealerSlice';

const store = configureStore({
    reducer: {
        chips: chipsReducer,
        game: gameReducer,
        player: playerReducer,
        dealer: dealerReducer,
    },
 });


export { store, 
    addChips, removeChips, resetChips, doubleChips, winChips, splitChips,
    setBetStage, setDealStage, setEvaluateStage, setScore, setSplitActive, setTextOutcome, setDealerTurnComplete, setGameOver, setHandsLost, setHandsWon, setHand1Dealt,
    setPlayerBet, setPlayerScore, playerDealCard, playerSplit, playerDealCard2, resetPlayerHand, setPlayerBet2,
    setDealerScore, dealerDealCard, resetDealerHand,



};
