import React, { useState } from "react";
import styles from '../index.module.css';
import Betting from '../components/betting';

interface DecisionProps {
  getWinnings: (winTotalFromChild: number) => void;
}

const Decision: React.FC<DecisionProps> = ({ getWinnings }) => {
  const [bet, setBet] = useState<number>(0);
  const [stack, setStack] = useState<number>(5000);
  const [winnings, setWinnings] = useState<number>(0);

  const handleChildBetAmount = (betAmountFromChild: number): void => {
    setBet(betAmountFromChild);
  };

  const handleChildStackAmount = (stackAmountFromChild: number): void => {
    setStack(stackAmountFromChild);
  };

  const onClickHandle = () => {
    // Double the current bet and add it to the winnings
    const totalWinnings = winnings + bet * 2;

    // Update the stack by adding the winnings
    setStack((prevStack) => prevStack + totalWinnings);

    // Reset the bet and set the new winnings
    setBet(0);
    setWinnings(totalWinnings);

    // Pass the updated stack to the Betting component
    handleChildStackAmount(stack + totalWinnings);
  };

  return (
    <>
      <div className={styles.row}>
        <div><h2>Bet total: {bet}</h2></div>
        <div><button className={styles.button} onClick={onClickHandle}>Deal</button></div>
        <div><button className={styles.button}>Hit</button></div>
        <div><button className={styles.button}>Stay</button></div>
        <div><button className={styles.button}>Split</button></div>
        <div><button className={styles.button}>Double</button></div>
        <div><h2>Stack: {stack}</h2></div>
      </div>
      <Betting betAmountChange={handleChildBetAmount} stackAmountChange={handleChildStackAmount} />
    </>
  );
};

export default Decision;
