import React, { useState } from "react";
import styles from '../index.module.css';

export interface BettingState {
  pot: number;
  stack: number;
}

interface BettingProps {
  updateBetTotal: (newBetTotal: number) => void;
  updatePot: (newPot: number) => void;
}

const Betting: React.FC<BettingProps> = (props) => {
  const [pot, setPot] = useState<number>(0);
  const [stack, setStack] = useState<number>(5000);

  const handleChipClick = (amount: number): void => {
    if (amount <= stack) {
      setPot((prevPot) => prevPot + amount);
      setStack((prevStack) => prevStack - amount);
      props.updateBetTotal((prevBetTotal: number) => prevBetTotal + amount);
    } else {
      alert("More than remaining bet amount. Try again.");
    }
  };

  const handleResetClick = (): void => {
    props.updatePot((prevPot: number) => prevPot + pot);
    props.updateBetTotal(0);
    setPot(0);
  };

  const handleAllInClick = (): void => {
    props.updatePot((prevPot: number) => prevPot + stack);
    props.updateBetTotal((prevBetTotal: number) => prevBetTotal + stack);
    setStack(0);
  };

  const chip = (amount: number): JSX.Element => (
    <div
      className={styles.chipcontainer}
      onClick={() => handleChipClick(amount)}
    >
      <div className={styles.textstyle}>{amount}</div>
      <img
        src="/pokerchip.png"
        alt="poker chip"
        width="100%"
        height="100%"
        className={styles.imageStyle}
      />
    </div>
  );

  const allinchip = (): JSX.Element => (
    <div
      className={styles.chipcontainer}
      onClick={() => handleAllInClick()}
    >
      <div className={styles.textstyle}>All In</div>
      <img
        src="/pokerchip.png"
        alt="poker chip"
        width="100%"
        height="100%"
        className={styles.imageStyle}
      />
    </div>
  );

  const resetchip = (): JSX.Element => (
    <div
      className={styles.chipcontainer}
      onClick={() => handleResetClick()}
    >
      <div className={styles.textstyle}>Reset</div>
      <img
        src="/pokerchip.png"
        alt="poker chip"
        width="100%"
        height="100%"
        className={styles.imageStyle}
      />
    </div>
  );

  return (
    <div className={styles.row}>
      {chip(1)}
      {chip(5)}
      {chip(10)}
      {chip(25)}
      {chip(100)}
      {chip(500)}
      {chip(1000)}
      {allinchip()}
      {resetchip()}
      <div className={styles.info}>
        <p>Pot: {pot}</p>
        <p>Stack: {stack}</p>
      </div>
    </div>
  );
};

export default Betting;
