import Chip from "./Chip";
import { useDispatch, useSelector } from "react-redux";
import { addChips } from "../../store/index";

const ChipsList = ()=>{
  const dispatch = useDispatch();
  const chips = useSelector((state: any) => state.chips);
  const handleChipClick = (amount: number) => {
    dispatch({type: "ADD_CHIPS", payload: amount});
  }
  const addChips = () => {
    dispatch({type: "ADD_CHIPS", payload: 100});
    console.log("add chips")
    console.log(addChips)
  }


  return (
      <>
      <div className="flex bg-yellow-500">
        <button onClick={addChips}>
          <h4>Restart</h4>
        </button>
        <Chip amount={1} color={"Red"} />
        <Chip amount={5} color={"Red"} />
        <Chip amount={10} color={"Red"} />
        <Chip amount={25} color={"Red"} />
        <div>
          {chips}
        </div>


      </div>


      </>
  )
}
export default ChipsList;
