        dispatch(setIsBlackjack(false));
        deck.resetDeck();
      };
      useEffect(() => {
        const calculatedHand2Score = calculateScore(hand2);
        dispatch(setPlayerScore2(calculatedHand2Score));
      }, [hand2]);
