import Header from "./Header/Header";
import CardsList from "./Cards/CardsList";
import ChipsList from "./Chips/ChipsList";
import ChoiceList from "./Choices/ChoiceList";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import Modal from "./ReusableForms/Modal";
import Button from "./ReusableForms/Button";

/*
import { useState } from 'react';
import Modal from '../components/Modal';
import Button from '../components/Button';

function ModalPage() {*/

const Table = () => {
    const { gameOver } = useSelector((state: any) => state.game);
    const { pot, stack } = useSelector((state: any) => state.player);
    const [showModal, setShowModal] = useState(false);
    useEffect(() => {
        if(pot === 0 && stack === 0){
            setShowModal(true);
        }

    }, [gameOver]);

  const handleClick = () => {
    setShowModal(true);
  };

  const handleClose = () => {
    setShowModal(false);
  };

  const actionBar = (
    <div>
      <Button onClick={handleClose} primary>
        I Accept
      </Button>
    </div>
  );
  const modal = (
    <Modal onClose={handleClose} actionBar={actionBar}>
      <p>Here is an important agreement for you to accept</p>
    </Modal>
  );

    return (<div className="flex flex-col border-2 border-white h-screen ">
        <Button onClick={handleClick} primary>
        Open Modal
      </Button>
        {showModal && modal}
        <Header />
        <CardsList />
        <ChoiceList />
        <ChipsList />

        <div>

        </div>
    </div>);
}
export default Table;
