import { configureStore } from '@reduxjs/toolkit';
import { chipsReducer, addChips, removeChips, resetChips } from './slices/chipsSlice';
import { gameReducer, setBetStage, setDealStage, setEvaluateStage, setScore } from './slices/gameSlice';
import { playerReducer, setPlayerBet, setPlayerScore, playerDealCard } from './slices/playerSlice';
import { dealerReducer, setDealerScore, dealerDealCard } from './slices/dealerSlice';

const store = configureStore({
    reducer: {
        chips: chipsReducer,
        game: gameReducer,
        player: playerReducer,
        dealer: dealerReducer,
    },
 });


export { store, 
    addChips, removeChips, resetChips,
    setBetStage, setDealStage, setEvaluateStage, setScore,
    setPlayerBet, setPlayerScore, playerDealCard,
    setDealerScore, dealerDealCard,



};
