import Choice from "./Choice";
import Button from "../ReusableForms/Button";
import { useDispatch, useSelector } from "react-redux";
import { playerDealCard, setBetStage, setDealStage, dealerDealCard } from "../../store";
import { useEffect } from "react";

// Card.ts
class Card {
  suit: string;
  rank: string;
  code: string;
  value: number;

  constructor(suit: string, rank: string) {
    this.suit = suit;
    this.rank = rank;
    this.code = `${rank}${suit[0]}`;
    this.value = this.calculateValue(rank);

    }
    calculateValue(rank: string): number {
      const faceCards = ['K', 'Q', 'J','0'];
      if (faceCards.includes(rank)) {
        return 10;
      } else if (rank === 'A') {
        return 1;
      } else {
        return parseInt(rank, 10);
      }
    }

  toString(): string {
    return `${this.rank} of ${this.suit} ${this.code} ${this.value}`;
  }
}
class Deck {
  cards: Card[];

  constructor() {
    this.cards = [];
    this.initializeDeck();
  }

  initializeDeck(): void {
    const suits: string[] = ['Hearts', 'Diamonds', 'Clubs', 'Spades'];
    const ranks: string[] = ['2', '3', '4', '5', '6', '7', '8', '9', '0', 'J', 'Q', 'K', 'A'];


    suits.forEach((suit) => {
      ranks.forEach((rank) => {
        const card = new Card(suit, rank);
        this.cards.push(card);
      });
    });
  }

  shuffle(): void {
    // Fisher-Yates shuffle algorithm
    for (let i = this.cards.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [this.cards[i], this.cards[j]] = [this.cards[j], this.cards[i]];
    }
  }

  dealCard(): Card | null {
    if (this.cards.length === 0) {
      console.error('No cards left in the deck.');
      return null;
    }
    return this.cards.pop()!;
  }

  resetDeck(): void {
    this.cards = [];
    this.initializeDeck();
    this.shuffle();
  }
}

// Example usage:

// Create a new deck
const deck = new Deck();



const ChoiceList = ()=>{
    const dispatch = useDispatch();
    const {betStage, dealStage, evaluateStage} = useSelector((state: any) => state.game);
    const {score} = useSelector((state: any) => state.player);
    const {dealerScore} = useSelector((state: any) => state.dealer);

    const handleDealClick = () => {
      if(betStage){
        const card = deck.dealCard();
        const card2 = deck.dealCard();
        const card3 = deck.dealCard();
        const card4 = deck.dealCard();
        console.log(card);
        dispatch(playerDealCard(card));
        dispatch(playerDealCard(card2));
        dispatch(dealerDealCard(card3));
        dispatch(dealerDealCard(card4));
        dispatch(setBetStage(false));
        dispatch(setDealStage(true));
      }
    };
      const handleHitClick = () => {
        if(!dealStage || score > 21){
          return;
        }
        const card = deck.dealCard();
        dispatch(playerDealCard(card));
      };
      const handleStandClick = () => {
        dispatch(setDealStage(false));

      };
      const handleSplitClick = () => {

      };
      const handleDoubleClick = () => {
        console.log('Clicked!');
      };
      useEffect(() => {
        const dealCardUntil17 = () => {
          if (dealerScore < 17) {
            const card = deck.dealCard();
            dispatch(dealerDealCard(card));
            // Recursive call to deal the next card with a delay
            setTimeout(dealCardUntil17, 1000); // Add a delay in milliseconds if needed
          }
        };

        if (evaluateStage) {
          dealCardUntil17();
        }
      }, [evaluateStage]);


  return (
      <>
      <div className="flex bg-red-600 items-center">
        <Button  primary rounded onClick={handleDealClick}>
          Deal
        </Button>
        <Button primary rounded onClick={handleHitClick}>
          Hit
        </Button>
        <Button primary rounded onClick={handleStandClick}>
          Stand
        </Button>
        <Button primary rounded onClick={handleDoubleClick}>
          Double
        </Button>
        <Button primary rounded onClick={handleSplitClick}>
          Split
        </Button>

      </div>
      </>
  )
}
export default ChoiceList;
