import React, { useState } from "react";
import styles from '../index.module.css';
import Betting, { BettingState } from '../components/betting'; // Import BettingState type

interface Props {
  getBettingState: () => BettingState; // Add a prop for the callback function
}

const Decision: React.FC<Props> = (props) => {
  const bettingState = props.getBettingState(); // Call the callback to get the Betting component state

  return (
    <>
      <div className={styles.row}>
        <div><h2>Bet total: {bettingState.pot}</h2></div>
        <div><button className={styles.button}>Deal</button></div>
        <div><button className={styles.button}>Hit</button></div>
        <div><button className={styles.button}>Stay</button></div>
        <div><button className={styles.button}>Split</button></div>
        <div><button className={styles.button}>Double</button></div>
        <div><h2>Pot: {bettingState.stack}</h2></div>
      </div>
      <Betting />
    </>
  );
}

export default Decision;
