import * as stateActions from '../../store';
import {deck} from '../Deck/deck';
import {Dispatch} from 'redux';

const handleDealClick = (dispatch: Dispatch, betStage: boolean, pot: number) => {

    if(betStage && pot > 0){
      dispatch(stateActions.setPlayerBet(pot));
      deck.shuffle();
      console.log(deck);
      const card = deck.dealCard();
      const card2 = deck.dealCard();
      const card3 = deck.dealCard();
      const card4 = deck.dealCard();
      dispatch(stateActions.playerDealCard(card));
      dispatch(stateActions.playerDealCard(card2));
      dispatch(stateActions.dealerDealCard(card3));
      dispatch(stateActions.dealerDealCard(card4));
      dispatch(stateActions.setBetStage(false));
      dispatch(stateActions.setDealStage(true));
    }
  };
  const handleHitClick = (dispatch: Dispatch, dealStage: boolean, splitActive: boolean, score: number, hand1dealt) => {
    if(!dealStage || (!splitActive && score> 21)){
      return;
    }
    if(score > 21){
      dispatch(setHand1Dealt(true));
    }
    if(!splitActive){
      const card = deck.dealCard();
      dispatch(playerDealCard(card));
    } else if(splitActive && !hand1dealt){
      const card = deck.dealCard();
      dispatch(playerDealCard(card));
    } else if(splitActive && hand1dealt){
      const card = deck.dealCard();
      dispatch(playerDealCard2(card));
    }
  };

export { handleDealClick, handleHitClick};
