import { configureStore } from '@reduxjs/toolkit';
import { chipsReducer, addChips, removeChips, resetChips, doubleChips } from './slices/chipsSlice';
import { gameReducer, setBetStage, setDealStage, setEvaluateStage, setScore, setSplitActive, set } from './slices/gameSlice';
import { playerReducer, setPlayerBet, setPlayerScore, playerDealCard, playerSplit } from './slices/playerSlice';
import { dealerReducer, setDealerScore, dealerDealCard } from './slices/dealerSlice';

const store = configureStore({
    reducer: {
        chips: chipsReducer,
        game: gameReducer,
        player: playerReducer,
        dealer: dealerReducer,
    },
 });


export { store, 
    addChips, removeChips, resetChips, doubleChips,
    setBetStage, setDealStage, setEvaluateStage, setScore, setSplitActive,
    setPlayerBet, setPlayerScore, playerDealCard, playerSplit,
    setDealerScore, dealerDealCard,



};
