import { useEffect } from "react";
import { text } from "stream/consumers";

// Card.ts
class Card {
  suit: string;
  rank: string;
  code: string;
  value: number;

  constructor(suit: string, rank: string) {
    this.suit = suit;
    this.rank = rank;
    this.code = `${rank}${suit[0]}`;
    this.value = this.calculateValue(rank);

    }
    calculateValue(rank: string): number {
      const faceCards = ['K', 'Q', 'J','0'];
      if (faceCards.includes(rank)) {
        return 10;
      } else if (rank === 'A') {
        return 1;
      } else {
        return parseInt(rank, 10);
      }
    }

  toString(): string {
    return `${this.rank} of ${this.suit} ${this.code} ${this.value}`;
  }
}
class Deck {
  cards: Card[];

  constructor() {
    this.cards = [];
    this.initializeDeck();
  }

  initializeDeck(): void {
    const suits: string[] = ['Hearts', 'Diamonds', 'Clubs', 'Spades'];
    const ranks: string[] = ['2', '3', '4', '5', '6', '7', '8', '9', '0', 'J', 'Q', 'K', 'A'];


    suits.forEach((suit) => {
      ranks.forEach((rank) => {
        const card = new Card(suit, rank);
        this.cards.push(card);
      });
    });
  }

  shuffle(): void {
    // Fisher-Yates shuffle algorithm
    for (let i = this.cards.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [this.cards[i], this.cards[j]] = [this.cards[j], this.cards[i]];
    }
  }
  blackjack(): void {
    const blackjackCards = this.cards.filter((card) => card.value === 10 || card.value === 1);
    this.cards = blackjackCards;
  }

  dealCard(): Card | null {
    if (this.cards.length === 0) {
      console.error('No cards left in the deck.');
      return null;
    }
    return this.cards.pop()!;
  }

  resetDeck(): void {
    this.cards = [];
    this.initializeDeck();
    this.shuffle();
  }
}

// Example usage:

// Create a new deck
const deck = new Deck();

const calculateScore = ((hand: any)=>{
  let score = 0;
  let aces = 0;
  let blackjack = false;
  hand.forEach((card: any) => {
    if(card.rank === 'A'){
      aces++;
    }
    score += card.value;
  });

  if(aces > 0 && score + 10 <= 21){
    score += 10;
  }
  if(hand.length === 2 && score === 21){
    blackjack = true;
  }

  return score;

})



