import React from 'react';
import Chip from "./Chip";
import { useDispatch, useSelector } from "react-redux";
import { addChips } from "../../store/index";

const ChipsList = () => {
  const dispatch = useDispatch();
  const chips  = useSelector((state: any) => state.chips);

  const handleChipClick = (amount: number) => {
    dispatch({ type: "chips/addChips", payload: amount });
  }

  return (
    <>
      <div className="flex bg-yellow-500">
        <button onClick={() => handleChipClick(100)}>
          <h4>Restart</h4>
        </button>
        <Chip onClick={handleChipClick} amount={1} color={"Red"} />
        <Chip onClick={handleChipClick} amount={5} color={"Red"} />
        <Chip onClick={handleChipClick} amount={10} color={"Red"} />
        <Chip onClick={handleChipClick} amount={25} color={"Red"} />

        <div>
          {chips}
        </div>
      </div>
    </>
  )
}

export default ChipsList;
